/*!/wp-content/plugins/mailbluster4wp/public/js/mailbluster4wp-public.js*/(function($){'use strict';$(function(){var $form=$('.mb4wp-s-form');$form.each(function(){var $this=$(this);var post_id=$this.find('.mb4wp-form-post-id').val();var $submit=$('.mb4wp-subscribe');$this.on('submit',function(e){e.preventDefault();var key='mb4wp_sform_'+post_id,formData=$this.find('input[name^="'+key+'"], select[name^="'+key+'"]').serialize();var data={};data.action='mb4wp_form_process';data.security=mb4wpAjaxForm.form_nonce;data[key]=formData;data.post_id=post_id;$.ajax({url:mb4wpAjaxForm.ajax_url,type:'POST',data:data,success:function(response){$this.find("input").val("");$this.find("select").find('option:first').attr('selected','selected');$this.hide();$this.parent().find('#mb4wp-form-messages').html(response)},error:function(response){$this.parent().find('#mb4wp-form-messages').html(response)}})})})})})(jQuery);